"use client";

// src/remotion/components/scenes/ABlueTriangleScene.tsx
import React from 'react';
import {
	AbsoluteFill,
	useCurrentFrame,
	useVideoConfig,
	interpolate,
	Easing,
} from 'remotion';
// import { AnimationDesignBrief } from '~/lib/schemas/animationDesignBrief.schema';

interface ABlueTriangleSceneProps {
	brief: AnimationDesignBrief;
}

export const ABlueTriangleScene: React.FC<ABlueTriangleSceneProps> = ({ brief }) => {
	const frame = useCurrentFrame();
	const { fps, width, height, durationInFrames } = useVideoConfig();

	// Extract palette from brief with fallbacks
	const {
		colorPalette: { primary = '#007BFF', background = '#FFFFFF' } = {},
	} = brief;

	// Timeline markers
	const fadeInStart = 0;
	const fadeInEnd = 60; // 2s at 30fps
	const scaleStart = fadeInEnd;
	const scaleEnd = scaleStart + 90; // next 3s
	const explodeStart = scaleEnd;
	const explodeEnd = explodeStart + 30; // final 1s

	// Opacity animation: fade in, then constant, then fade out
	const opacity = frame < fadeInEnd
		? interpolate(frame, [fadeInStart, fadeInEnd], [0, 1], {
			extrapolateLeft: 'clamp',
			extrapolateRight: 'clamp',
			easing: Easing.easeOutCubic,
		})
		: frame < explodeStart
t		? 1
		: interpolate(frame, [explodeStart, explodeEnd], [1, 0], {
			extrapolateLeft: 'clamp',
			extrapolateRight: 'clamp',
			easing: Easing.easeInCubic,
		});

	// Scale animation: initial, grow, then explode
	const scale = frame < scaleStart
		? 1
		: frame < explodeStart
		? interpolate(frame, [scaleStart, scaleEnd], [1, 2], {
			extrapolateLeft: 'clamp',
			extrapolateRight: 'clamp',
			easing: Easing.easeOutBack,
		})
		: interpolate(frame, [explodeStart, explodeEnd], [2, 3], {
			extrapolateLeft: 'clamp',
			extrapolateRight: 'clamp',
			easing: Easing.easeInCubic,
		});

	// Styles for the triangle: centered via translate(-50%,-50%)
	const triangleSize = 300;
	const triangleStyle: React.CSSProperties = {
		position: 'absolute',
		left: width / 2,
		top: height / 2,
		width: triangleSize,
		height: triangleSize,
		opacity,
		backgroundColor: primary,
		clipPath: 'polygon(50% 0%, 0% 100%, 100% 100%)',
		transform: `translate(-50%, -50%) scale(${scale})`,
	};

	return (
		<AbsoluteFill style={{ backgroundColor: background }}>
			{/* Blue triangle with fade-in, scale-up, and explosion */}
			<div style={triangleStyle} />
		</AbsoluteFill>
	);
};
